mapper.update:
update exam_paper
set exam_code = #{examCode,jdbcType=VARCHAR},
  paper_code = #{paperCode,jdbcType=VARCHAR},
  del_flag = #{delFlag,jdbcType=TINYINT}
where exam_paper_id = #{examPaperId,jdbcType=BIGINT}

mapper.select:
select exam_paper_id, exam_code, paper_code, del_flag
from exam_paper
where exam_paper_id = #{examPaperId,jdbcType=BIGINT}
