mapper.update:
update students_exam
set exam_code = #{examCode,jdbcType=VARCHAR},
  stud_id = #{studId,jdbcType=VARCHAR},
  del_flag = #{delFlag,jdbcType=TINYINT}
where stud_exam_id = #{studExamId,jdbcType=BIGINT}

mapper.select:
select stud_exam_id, exam_code, stud_id, del_flag
from students_exam
where stud_exam_id = #{studExamId,jdbcType=BIGINT}
